'use strict';

var path = require('path');
var utils = require('./utils');
var list = require('./list');

module.exports = function(app, base, env, options) {
  var gm = path.resolve.bind(path, utils.gm);
  var cwd = path.resolve.bind(path, app.cwd);

  /**
   * Register sub-generators (as plugins)
   */

  app.use(require('generate-collections'));
  app.use(require('generate-defaults'));

  /**
   * Generate an `assemblefile.js`
   */

  app.task('new', function(cb) {
    app.src('templates/generator.js', {cwd: __dirname})
      .pipe(app.dest(app.options.dest || app.cwd))
      .on('end', function() {
        console.log(utils.log.timestamp, 'created generator.js');
        cb();
      });
  });

  /**
   * Display a list of installed generators.
   *
   * ```sh
   * $ gen list
   * ```
   * @name list
   * @api public
   */

  app.task('list', { silent: true }, function() {
    return app.src([gm('generate-*'), cwd('node_modules/generate-*')])
      .pipe(utils.through.obj(function(file, enc, next) {
        file.alias = app.toAlias(file.basename);
        next(null, file);
      }))
      .pipe(list(app));
  });

  app.task('tasks', { silent: true }, function(cb) {
    Object.keys(app.tasks).forEach(function(name) {
      console.log(' Â· ' + app.log.cyan(name));
    });
    cb();
  });

  /**
   * Display `--help`.
   *
   * ```sh
   * $ gen defaults:help
   * ```
   * @name help
   * @api public
   */

  app.task('help', { silent: true }, function(cb) {
    app.enable('silent');
    app.cli.process({ help: true }, cb);
  });

  /**
   * Render a single `--src` file to the given `--dest` or current working directory.
   *
   * ```sh
   * $ gen defaults:render
   * # aliased as
   * $ gen render
   * ```
   * @name render
   * @api public
   */

  app.task('render', function(cb) {
    if (!app.option('src')) {
      app.emit('error', new Error('Expected a `--src` filepath'));
    } else if (!app.option('dest')) {
      app.build(['dest', 'render'], cb);
    } else {
      file(app, app.option('src'), app.cwd, cb);
    }
  });

  /**
   * Default task
   */

  app.task('default', ['help']);
};

function file(app, name, templates, dest, cb) {
  if (typeof dest === 'function') {
    cb = dest;
    dest = null;
  }

  dest = dest || app.option('dest') || '';
  app.src(name, {cwd: templates})
    .pipe(app.renderFile('*'))
    .pipe(app.conflicts(dest))
    .pipe(app.dest(dest))
    .on('error', cb)
    .on('end', cb);
}

'use strict';

var path = require('path');
var utils = require('./utils');

module.exports = function(app) {
  function bold(str) {
    return app.log.underline(app.log.bold(str));
  }

  var list = [[bold('version'), bold('name'), bold('alias')]];
  var cache = {};

  return utils.through.obj(function(file, enc, next) {
    if (cache[file.stem]) {
      next();
      return;
    }

    cache[file.stem] = true;
    if (utils.blacklist.indexOf(file.stem) !== -1) {
      next();
      return;
    }

    var pkgPath = path.resolve(file.path, 'package.json');
    var pkg = require(pkgPath);
    list.push([app.log.gray(pkg.version), file.basename, app.log.cyan(file.alias)]);
    next();
  }, function(cb) {
    console.log();
    console.log(utils.table(list, {
      stringLength: function(str) {
        return utils.strip(str).length;
      }
    }));

    console.log();
    console.log(app.log.magenta(list.length + ' generators installed'));
    console.log();
    cb();
  });
};

'use strict';

var utils = require('./utils');

/**
 * Determine the task to run. This is only temporary until we get
 * generators published for handling the built-in tasks.
 */

module.exports = function(app, ctx, argv) {
  var configFile = ctx.env.configFile;

  // determine the tasks to run (returns the first value that isn't `["default"]` or `[]`)
  var tasks = utils.getTasks(configFile, [
    argv._, // command line
    ctx.pkgConfig.tasks, // set in package.json
    app.store.get('defaultTasks') // stored user-defined "default" tasks
  ]);

  tasks = tasks || [];

  // no tasks are defined
  if (tasks.length === 0) {
    // if `generator.js` exists in cwd, call it's default task
    if (utils.exists(configFile)) {
      return ['default:default'];
    }
    return ['defaults:help'];
  }

  // at least one task was defined
  tasks = tasks.reduce(function(acc, name) {
    if (name.indexOf('help') === 0) {
      return acc.concat('defaults:help');
    }
    if (name.indexOf('list') === 0) {
      return acc.concat('defaults:list');
    }
    if (name.indexOf('tasks') === 0) {
      return acc.concat('defaults:tasks');
    }
    if (name.indexOf('store') === 0) {
      return acc.concat('defaults.' + name);
    }
    acc.push(name);
    return acc;
  }, []);

  if (tasks.length === 1) {
    var task = tasks[0];
    switch (task) {
      case 'default':
        // if a `generator.js` exists in user's cwd, run its default task
        if (utils.exists(configFile)) {
          return ['default:default'];
        }
        // fall through
      case 'help':
        return ['defaults:help'];
      case 'new':
        return ['defaults:new'];
      case 'render':
        return ['defaults:render'];
      default:
        return tasks;
    }
  }
  return tasks;
};
